mapper.delete:
delete from tbl_schedule
	where user_code = #{user_code}
	and	  sch_date = #{sch_date}
	and	  sch_time = #{sch_time}

mapper.update:
update tbl_schedule
set sch_content = #{sch_content}
where user_code = #{user_code}
and	  sch_date = #{sch_date}
and	  sch_time = #{sch_time}

mapper.select:
select *
from tbl_schedule
where user_code = #{user_code}
and sch_date = #{sch_date}
